CREATE OR REPLACE PROCEDURE atendimento_acomp_beforepost ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, qt_perm_acomp_p INOUT bigint, qt_registros_p INOUT bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_forma_cons_acomp_w	varchar(5);
qt_perm_acomp_w		smallint	:= 0;
qt_registros_w		bigint	:= 0;


BEGIN

if (nr_atendimento_p > 0) then
	begin

	ie_forma_cons_acomp_w := Obter_Param_Usuario(44, 76, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, ie_forma_cons_acomp_w);

	if (ie_forma_cons_acomp_w = 'EA') then
		select	coalesce(sum(qt_max_acomp),0)
		into STRICT	qt_perm_acomp_w
		from	unidade_atendimento
		where	cd_setor_atendimento	= cd_setor_atendimento_p
		and	cd_unidade_basica	= cd_unidade_basica_p
		and	cd_unidade_compl	= cd_unidade_compl_p;

	elsif (ie_forma_cons_acomp_w = 'UEU') then
		select 	max(nr_acompanhante)
		into STRICT 	qt_perm_acomp_w
		from 	atend_categoria_convenio
		where 	nr_atendimento = nr_atendimento_p
		and 	nr_seq_interno = (SELECT max(nr_seq_interno) from atend_categoria_convenio where nr_atendimento = nr_atendimento_p);

	else
		select	coalesce(sum(nr_acompanhante),0)
		into STRICT	qt_perm_acomp_w
		from	atend_categoria_convenio
		where	nr_atendimento = nr_atendimento_p;

	end if;

	select	count(*)
	into STRICT	qt_registros_w
	from	atendimento_acompanhante
	where	nr_atendimento = nr_atendimento_p
	and	coalesce(dt_saida::text, '') = '';

	end;
end if;

qt_perm_acomp_p	:= qt_perm_acomp_w;
qt_registros_p	:= qt_registros_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
