SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atendimento_visita_saida_auto () AS $body$
DECLARE

nr_sequencia_w	bigint;

C01 CURSOR FOR	/*   visitantes sem data saida e com data entrada < 24horas */
	SELECT	nr_sequencia
	from	atendimento_visita
	where	coalesce(dt_saida::text, '') = ''
	and (dt_entrada +1) <= clock_timestamp();


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update 	atendimento_visita
	set	dt_saida = clock_timestamp()
	where	nr_sequencia = nr_sequencia_w;

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atendimento_visita_saida_auto () FROM PUBLIC;

