CREATE OR REPLACE PROCEDURE atend_pac_befpost_confir_eup ( cd_pessoa_responsavel_p text, cd_pessoa_fisica_p text, ie_exige_cpf_resp_w text, nm_usuario_p text, ds_msg_erro_p INOUT text, ds_msg_alerta_cpf_resp_p INOUT text) AS $body$
DECLARE


ds_msg_erro_w	varchar(255);
ds_msg_alerta_cpf_resp_w	varchar(255);
ie_continuar_execucao_w	varchar(1) := 'S';
ie_possui_cpf_w		varchar(1);


BEGIN

if (ie_continuar_execucao_w = 'S') and (ie_exige_cpf_resp_w <> 'N') and (coalesce(cd_pessoa_responsavel_p::text, '') = '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_cpf_w
	from	pessoa_fisica
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	(nr_cpf IS NOT NULL AND nr_cpf::text <> '');

	if (ie_possui_cpf_w = 'N') then
		if (ie_exige_cpf_resp_w = 'S') then
			ds_msg_erro_w := substr(obter_texto_tasy(196695, wheb_usuario_pck.get_nr_seq_idioma),1,255);
			ie_continuar_execucao_w := 'N';
		else
			ds_msg_alerta_cpf_resp_w := substr(obter_texto_tasy(196695, wheb_usuario_pck.get_nr_seq_idioma),1,255);
			ie_continuar_execucao_w := 'S';
		end if;
	end if;
end if;

ds_msg_alerta_cpf_resp_p := ds_msg_alerta_cpf_resp_w;
ds_msg_erro_p := ds_msg_erro_w;


commit;

end;
$body$
