CREATE OR REPLACE PROCEDURE atend_unid_newre_abort_js ( ie_regra_conv_atend_p text, cd_estabelecimento_p bigint, ie_conv_exig_cpf_p text, ie_exige_cpg_rg_estr_p text, nr_atendimento_p bigint, ie_tipo_atendimento_p bigint, cd_convenio_p bigint, cd_pessoa_fisica_p text, cd_pessoa_resp_p text, nm_usuario_p text, ds_msg_abort_cpf_p INOUT text, ds_msg_abort_p INOUT text) AS $body$
DECLARE

 
ie_exige_cpf_paciente_w		varchar(1);	
ds_msg_abort_w			varchar(255);	
ie_exige_cpf_w 			varchar(1);			
					 

BEGIN 
 
-- ie_exige_cpg_rg_estr_p param 665, EUP 
 
select	max(ie_exige_cpf_paciente) 
into STRICT	ie_exige_cpf_paciente_w        
from 	convenio_estabelecimento  
where 	cd_convenio = cd_convenio_p        
and	cd_estabelecimento = cd_estabelecimento_p;
 
ie_exige_cpf_w := obter_se_exige_cpf_js(cd_pessoa_fisica_p, cd_pessoa_resp_p, ie_conv_exig_cpf_p);
 
 
if (ie_exige_cpg_rg_estr_p = 'S') and (ie_exige_cpf_paciente_w = 'S') then 
 
	ds_msg_abort_w := verifica_cpf_pf_atendimento(nr_atendimento_p, cd_convenio_p, ie_tipo_atendimento_p, ds_msg_abort_w);
	if (ds_msg_abort_w IS NOT NULL AND ds_msg_abort_w::text <> '') then 
		ds_msg_abort_cpf_p := ds_msg_abort_w;
		goto final;
	end if;
end if;
 
 
 
if (ie_exige_cpf_w = 'S') and (ie_exige_cpf_paciente_w = 'S') then 
	 
	update	atendimento_paciente 
	set	ie_tipo_convenio = obter_tipo_convenio(cd_convenio_p) 
    where	nr_atendimento = nr_atendimento_p;
	 
	ds_msg_abort_cpf_p := substr(wheb_mensagem_pck.get_texto(82814, null),1,255);
	goto final;
end if;
 
if (ie_regra_conv_atend_p = 'S') then 
	ds_msg_abort_p := Verifica_Conv_Regra_Atend(nr_atendimento_p, cd_estabelecimento_p, null, ds_msg_abort_p);
	goto final;
end if;
 
<<final>> 
 
commit;
 
end;
$body$
