CREATE OR REPLACE PROCEDURE atepac_altera_status_auditado ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
	ie_mat_auditado_w varchar(1);
	

BEGIN 
 
select 	coalesce(max(ie_calcular_mat_auditado), 'S') 
into STRICT	ie_mat_auditado_w 
from  	parametro_faturamento 
where 	cd_estabelecimento = cd_estabelecimento_p;
 
if (ie_mat_auditado_w = 'N') then 
	begin 
	 
update 	material_atend_paciente 
set  	ie_auditoria = 'S', 
	nm_usuario = nm_usuario_p 
where 	nr_sequencia = nr_sequencia_p;	
		 
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
