CREATE OR REPLACE PROCEDURE ativar_inativar_unidade ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, nm_usuario_p text) AS $body$
BEGIN

update 	Unidade_atendimento
set 	ie_situacao		= CASE WHEN ie_situacao='A' THEN  'I'  ELSE 'A' END ,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where 	cd_setor_atendimento	= cd_setor_atendimento_p
and	cd_unidade_basica		= cd_unidade_basica_p
and	cd_unidade_compl		= cd_unidade_compl_p;

commit;

END;
