CREATE OR REPLACE PROCEDURE ativar_intativar_laudo_pac_reg ( ie_situacao_p text, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_situacao_aux_w 	varchar(1);


BEGIN

if (ie_situacao_p = 'S') then
	ie_situacao_aux_w := 'N';
else
	ie_situacao_aux_w := 'S';
end if;

update 	laudo_paciente_reg_result
set    	ie_situacao = ie_situacao_aux_w,
	dt_inativacao = CASE WHEN ie_situacao_aux_w='N' THEN  clock_timestamp()  ELSE null END ,
	nm_usuario = nm_usuario_p ,
	dt_atualizacao = clock_timestamp()
where  	nr_sequencia = nr_sequencia_p;

commit;

end;
$body$
