SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ativar_leitos_agrupamento_sl ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, nm_usuario_p text) AS $body$
DECLARE


nr_agrupamento_w			integer;
qt_unidade_ocupado_w		integer;
ie_atualizar_leitos_temp_w	varchar(1);



BEGIN
ie_atualizar_leitos_temp_w := Obter_Param_Usuario(75, 42, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_atualizar_leitos_temp_w);

select	max(nr_agrupamento)
into STRICT	nr_agrupamento_w
from	unidade_atendimento
where	cd_setor_atendimento 	= cd_setor_atendimento_p
and	cd_unidade_basica	= cd_unidade_basica_p
and	cd_unidade_compl	= cd_unidade_compl_p
and	ie_inativar_leito_temp  = 'S'
and	ie_temporario		= 'S';


if (nr_agrupamento_w IS NOT NULL AND nr_agrupamento_w::text <> '') then

	update 	unidade_atendimento
	set	   	ie_situacao 	= 'A',
			nm_usuario		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
	where  	cd_setor_atendimento = cd_setor_atendimento_p
	and	   	nr_agrupamento = nr_agrupamento_w
	and (cd_unidade_basica <> cd_unidade_basica_p
			or cd_unidade_compl <> cd_unidade_compl_p)
	and 	( (ie_atualizar_leitos_temp_w = 'S')
			or ((ie_atualizar_leitos_temp_w = 'T')
				and (coalesce(ie_temporario,'N') <> 'S')));
	
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
