SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ativar_leitos_agrupamento ( cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, nm_usuario_p text) AS $body$
DECLARE


nr_agrupamento_w		integer;
qt_unidade_ocupado_w		integer;


BEGIN

select	max(nr_agrupamento)
into STRICT	nr_agrupamento_w
from	unidade_atendimento
where	cd_setor_atendimento 	= cd_setor_atendimento_p
and	cd_unidade_basica	= cd_unidade_basica_p
and	cd_unidade_compl	= cd_unidade_compl_p
and	ie_inativar_leito_temp  = 'S'
and	ie_temporario		= 'S'
and	ie_status_unidade	= 'L'
and	ie_situacao		= 'I';

if (nr_agrupamento_w IS NOT NULL AND nr_agrupamento_w::text <> '') then

	update 	unidade_atendimento
	set	ie_situacao 		= 'A',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	cd_setor_atendimento 	= cd_setor_atendimento_p
	and	nr_agrupamento 		= nr_agrupamento_w
	and (cd_unidade_basica <> cd_unidade_basica_p or
		 cd_unidade_compl  <> cd_unidade_compl_p);

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
