SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_acomp_paciente ( nr_sequencia_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

  qt_acomp_pac_w bigint;

BEGIN

  SELECT COUNT(*)
  INTO STRICT qt_acomp_pac_w
  FROM atendimento_acompanhante
  WHERE nr_atendimento = nr_atendimento_p
  AND coalesce(dt_saida::text, '') = '';

  UPDATE atend_paciente_unidade
  SET nr_acompanhante = qt_acomp_pac_w,
    dt_atualizacao    = clock_timestamp(),
    nm_usuario        = nm_usuario_p
  WHERE nr_sequencia  = nr_sequencia_p
  AND nr_atendimento  = nr_atendimento_p;

  COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
