CREATE OR REPLACE PROCEDURE atualizar_agenda_consulta_js ( ds_lista_agenda_p text, nr_atendimento_p bigint, nr_seq_agenda_p bigint, nr_acao_executada_p bigint, cd_tipo_agenda_p bigint, ie_agenda_cons_nova_p text, ie_parametro_externo_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_prescricao_p INOUT bigint) AS $body$
DECLARE

 
ie_evento_w	varchar(1);
nr_prescricao_w	bigint;
				

BEGIN 
 
if (ds_lista_agenda_p <> '')then 
	begin 
	 
	CALL atualizar_lista_ag_cons_eup(nr_atendimento_p, ds_lista_agenda_p, nm_usuario_p);
	 
	end;
else 
	begin 
	 
	CALL atualizar_atend_ag_cons_eup(nr_atendimento_p, nr_seq_agenda_p, nm_usuario_p);
	 
	end;
end if;
 
if (nr_acao_executada_p = 1)then 
	begin 
	 
	CALL atualizar_espec_med_eup_agenda(nr_atendimento_p, nr_seq_agenda_p, nm_usuario_p);
	 
	end;
end if;
 
if (cd_tipo_agenda_p in (3,4))and (ie_agenda_cons_nova_p <> 'S')then 
	begin 
	 
	ie_evento_w	:= obter_se_existe_evento_agenda(cd_estabelecimento_p,'GA','C');
	 
	if (ie_evento_w = 'N')and (ie_parametro_externo_p = 'N')then 
		begin 
		 
		CALL Atualizar_Status_Ag_Cons_EUP(nr_seq_agenda_p, 'O', nm_usuario_p);
		 
		end;
	end if;
	 
	end;
end if;
 
CALL gerar_prescr_agenda_consulta(nr_seq_agenda_p, nm_usuario_p);
 
select coalesce(max(nr_prescricao),0) 
into STRICT	nr_prescricao_w 
from 	prescr_medica  
where 	nr_seq_agecons = nr_seq_agenda_p;
 
nr_prescricao_p	:= nr_prescricao_w;
 
commit;
 
end;
$body$
