SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_agenda_exames_presc ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE

						
cd_pessoa_fisica_w	varchar(10);
nr_atendimento_w	bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
nr_seq_proc_interno_w	bigint;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

	select 	a.cd_pessoa_fisica,
		a.nr_atendimento,
		b.cd_procedimento,
		b.ie_origem_proced,
		b.nr_seq_proc_interno
	into STRICT	cd_pessoa_fisica_w,
		nr_atendimento_w,
		cd_procedimento_w,
		ie_origem_proced_w,
		nr_seq_proc_interno_w
	from	prescr_medica a,
		prescr_procedimento b
	where	a.nr_prescricao = b.nr_prescricao
	and	a.nr_prescricao = nr_prescricao_p
	and	b.nr_sequencia = nr_seq_prescr_p;
		
	update	agenda_paciente
	set	cd_procedimento 	= cd_procedimento_w,
		ie_origem_proced	= ie_origem_proced_w,
		nr_seq_proc_interno 	= nr_seq_proc_interno_w,
		cd_pessoa_fisica 	= cd_pessoa_fisica_w,
		nm_paciente	 	= obter_nome_pf(cd_pessoa_fisica_w),
		dt_nascimento_pac      	= obter_data_nascto_pf(cd_pessoa_fisica_w),
		qt_idade_paciente	= obter_dados_pf(cd_pessoa_fisica_w,'I'),
		nr_telefone		= obter_fone_pac_agenda(cd_pessoa_fisica_w),
		cd_procedencia		= obter_procedencia_pf(cd_pessoa_fisica_w),		
		ie_status_agenda = 'N'
	where 	nr_sequencia = nr_seq_agenda_p;		
	
	update	prescr_medica
	set	nr_seq_agenda		= nr_seq_agenda_p
	where	nr_prescricao		= nr_prescricao_p;
		
	CALL vincular_atend_agenda(2,nr_seq_agenda_p,nr_atendimento_w,nm_usuario_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
