CREATE OR REPLACE PROCEDURE atualizar_agenda_pac_opme ( nr_seq_autorizacao_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_material_w			integer;
nr_seq_pac_opme_w		bigint;
ie_aprovacao_w			varchar(1);
ie_atualizar_aprovado_w		varchar(1);

c01 CURSOR FOR
	SELECT	a.cd_material,
		a.nr_seq_opme,
		a.ie_aprovacao
	from	material_autor_cirurgia a,
		autorizacao_cirurgia b
	where	a.nr_seq_autorizacao 	= b.nr_sequencia
	and	b.nr_sequencia		= nr_seq_autorizacao_p
	and	b.nr_seq_agenda		= nr_seq_agenda_p;


BEGIN

ie_atualizar_aprovado_w := obter_param_usuario(3006, 29, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_atualizar_aprovado_w);

open c01;
loop
fetch c01 into
	cd_material_w,
	nr_seq_pac_opme_w,
	ie_aprovacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (ie_aprovacao_w = 'S') then
		update	agenda_pac_opme
		set	ie_autorizado	= 'A',
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_seq_agenda	= nr_seq_agenda_p
		and	cd_material	= cd_material_w
		and	nr_sequencia	= coalesce(nr_seq_pac_opme_w,nr_sequencia);
	elsif (ie_aprovacao_w = 'N') and (ie_atualizar_aprovado_w = 'A') then
		update	agenda_pac_opme
		set	ie_autorizado	= 'N',
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_seq_agenda	= nr_seq_agenda_p
		and	cd_material	= cd_material_w
		and	nr_sequencia	= coalesce(nr_seq_pac_opme_w,nr_sequencia);
	end if;
	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
