CREATE OR REPLACE PROCEDURE atualizar_agenda_tasy_convite (nr_seq_agenda_p bigint, dt_agenda_p timestamp, ie_confirmado_p text, ds_motivo_ausencia_p text, nm_usuario_p text) AS $body$
DECLARE


ds_agenda_w				varchar(100);
nr_seq_tipo_w				bigint;
nr_minuto_duracao_w			bigint;
ds_local_w				varchar(50);
ds_observacao_w				varchar(2000);
nr_seq_agenda_w				bigint;
nr_seq_local_w				bigint;
nm_usuario_agenda_w			varchar(15);


BEGIN

select	ds_agenda,
	nr_seq_tipo,
	nr_minuto_duracao,
	ds_local,
	ds_observacao,
	nr_seq_local,
	nm_usuario_agenda
into STRICT	ds_agenda_w,
	nr_seq_tipo_w,
	nr_minuto_duracao_w,
	ds_local_w,
	ds_observacao_w,
	nr_seq_local_w,
	nm_usuario_agenda_w
from	agenda_tasy
where	nr_sequencia		= nr_seq_agenda_p;


update	agenda_tasy_convite
set	ie_confirmado		= ie_confirmado_p,
	ds_motivo_ausencia	= ds_motivo_ausencia_p,
	dt_atualizacao		= clock_timestamp(),
	dt_confirmacao		= trunc(clock_timestamp()),
	nm_usuario		= nm_usuario_p
where	nr_seq_agenda		= nr_seq_agenda_p
and	nm_usuario_convidado	= nm_usuario_p;



if 	((ie_confirmado_p	= 'S')  and (coalesce(nm_usuario_agenda_w,'XPTO') <> coalesce(nm_usuario_p,'XPTO'))) then
	begin
	delete	from agenda_tasy
	where	nm_usuario_agenda	= nm_usuario_p
	and	dt_agenda		= dt_agenda_p
	and	ie_status		= 'L';

	select	nextval('agenda_tasy_seq')
	into STRICT	nr_seq_agenda_w
	;

	insert	into agenda_tasy(nr_sequencia,
		ds_agenda,
		dt_agenda,
		nr_seq_tipo,
		nr_minuto_duracao,
		nm_usuario_agenda,
		dt_atualizacao,
		nm_usuario,
		ds_observacao,
		ds_local,
		ie_status,
		nr_seq_local,
		nr_seq_agenda_convite)
	values (nr_seq_agenda_w,
		ds_agenda_w,
		dt_agenda_p,
		nr_seq_tipo_w,
		nr_minuto_duracao_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_observacao_w,
		ds_local_w,
		'M',
		nr_seq_local_w,
		nr_seq_agenda_p);

	end;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
