SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_anexo_conta (nr_sequencia_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
BEGIN

if (ie_opcao_p = 'L') then

	update 	conta_paciente_anexo
	set	dt_liberacao = clock_timestamp(),
		nm_usuario_lib = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 'D') then

	update 	conta_paciente_anexo
	set	dt_liberacao  = NULL,
		nm_usuario_lib  = NULL
	where	nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 'C') then

	update 	conta_paciente_anexo
	set	dt_conferencia = clock_timestamp(),
		nm_usuario_conf = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 'I') then

	update 	conta_paciente_anexo
	set	dt_inativacao = clock_timestamp(),
		nm_usuario_inativacao = nm_usuario_p,
		dt_conferencia = clock_timestamp(),
		nm_usuario_conf = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 'A') then

	update 	conta_paciente_anexo
	set	dt_conferencia  = NULL,
		nm_usuario_conf  = NULL,
		dt_inativacao  = NULL,
		nm_usuario_inativacao  = NULL
	where	nr_sequencia = nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
