SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_antimicrobiano ( nr_prescricao_p bigint, nr_seq_origem_p bigint, nr_seq_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_dia_util_w			bigint;
qt_dias_solicitado_w		smallint;
qt_dias_liberado_w			smallint;
nr_seq_avaliacao_w		bigint;
ie_objetivo_w			varchar(1);
cd_topografia_cih_w		bigint;
ie_origem_infeccao_w		varchar(1);
cd_amostra_cih_w			bigint;
cd_microorganismo_cih_w		bigint;
ie_cultura_cih_w			varchar(1);
ie_antibiograma_w			varchar(1);
ie_uso_antimicrobiano_w		varchar(1);
ie_indicacao_w			varchar(1);
ds_justificativa_w			varchar(2000);
cd_cid_item_w			varchar(10);
nr_sequencia_w			bigint;
count_w				bigint;


C01 CURSOR FOR
	SELECT	c.cd_doenca_cid
	from	prescr_material_cid c
	where	c.nr_sequencia_medic = nr_seq_origem_p
	and	c.nr_prescricao = nr_prescricao_p;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_origem_p IS NOT NULL AND nr_seq_origem_p::text <> '') and (nr_seq_destino_p IS NOT NULL AND nr_seq_destino_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	count(*),
		max(a.nr_dia_util),
		max(a.qt_dias_solicitado),
		max(a.qt_dias_liberado),
		max(a.nr_seq_avaliacao),
		max(a.ie_objetivo),
		max(a.cd_topografia_cih),
		max(a.ie_origem_infeccao),
		max(a.cd_amostra_cih),
		max(a.cd_microorganismo_cih),
		max(a.ie_cultura_cih),
		max(a.ie_antibiograma),
		max(a.ie_uso_antimicrobiano),
		max(a.ie_indicacao),
		max(a.ds_justificativa)
	into STRICT	count_w,
		nr_dia_util_w,
		qt_dias_solicitado_w,
		qt_dias_liberado_w,
		nr_seq_avaliacao_w,
		ie_objetivo_w,
		cd_topografia_cih_w,
		ie_origem_infeccao_w,
		cd_amostra_cih_w,
		cd_microorganismo_cih_w,
		ie_cultura_cih_w,
		ie_antibiograma_w,
		ie_uso_antimicrobiano_w,
		ie_indicacao_w,
		ds_justificativa_w
	from	prescr_material a
	where	a.nr_prescricao	= nr_prescricao_p
	and     	a.nr_sequencia  	= nr_seq_origem_p
	and	a.ie_agrupador	= 1;

	if (count_w > 0) then
		update	prescr_material
		set	qt_dias_solicitado	= qt_dias_solicitado_w,
			qt_dias_liberado	= qt_dias_liberado_w,
			nr_seq_avaliacao	= nr_seq_avaliacao_w,
			ie_objetivo		= ie_objetivo_w,
			cd_topografia_cih	= cd_topografia_cih_w,
			ie_origem_infeccao	= ie_origem_infeccao_w,
			cd_amostra_cih		= cd_amostra_cih_w,
			cd_microorganismo_cih	= cd_microorganismo_cih_w,
			ie_cultura_cih		= ie_cultura_cih_w,
			ie_antibiograma		= ie_antibiograma_w,
			ie_uso_antimicrobiano	= ie_uso_antimicrobiano_w,
			ie_indicacao		= ie_indicacao_w,
			ds_justificativa	= ds_justificativa_w
		where	nr_sequencia		= nr_seq_destino_p
		and	nr_prescricao		= nr_prescricao_p;
	end if;

	open C01;
	loop
	fetch C01 into
		cd_cid_item_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	nextval('prescr_material_cid_seq')
		into STRICT	nr_sequencia_w
		;

		if (cd_cid_item_w IS NOT NULL AND cd_cid_item_w::text <> '') then
			insert into prescr_material_cid(
				nr_sequencia,
				cd_doenca_cid,
				nr_prescricao,
				nr_sequencia_medic,
				dt_atualizacao,
				nm_usuario)
			values (	nr_sequencia_w,
				cd_cid_item_w,
				nr_prescricao_p,
				nr_seq_destino_p,
				clock_timestamp(),
				nm_usuario_p);
		end if;
		end;
	end loop;
	close C01;

	commit;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_antimicrobiano ( nr_prescricao_p bigint, nr_seq_origem_p bigint, nr_seq_destino_p bigint, nm_usuario_p text) FROM PUBLIC;

