CREATE OR REPLACE PROCEDURE atualizar_aprovacao_exame ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_resultado_p bigint, cd_medico_resp_p text, nm_usuario_p text default null) AS $body$
DECLARE


nr_regras_atendidas_w   varchar(2000);

BEGIN

update	exame_lab_result_item
set 	dt_aprovacao = clock_timestamp(),
	nm_usuario_aprovacao = nm_usuario_p,
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p,
	cd_medico_resp = cd_medico_resp_p
where 	nr_seq_resultado = nr_seq_resultado_p
and 	nr_seq_prescr = nr_seq_prescr_p
and 	(nr_seq_material IS NOT NULL AND nr_seq_material::text <> '');

update 	prescr_procedimento
set 	ie_status_atend = 35,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where 	nr_prescricao = nr_prescricao_p
and 	nr_sequencia = nr_seq_prescr_p
and 	ie_status_atend < 35;

commit;

CALL send_physio_data_integration(nr_prescricao_p, nr_seq_prescr_p, null, null, null,
null, null, null, null, null, clock_timestamp());

if (nr_seq_resultado_p IS NOT NULL AND nr_seq_resultado_p::text <> '') then
    nr_regras_atendidas_w := GQA_Aprov_Exame_result_item(nr_seq_resultado_p, nr_seq_prescr_p, nm_usuario_p, nr_regras_atendidas_w);
end if;

end;
$body$
