CREATE OR REPLACE PROCEDURE atualizar_atendimento_retorno_atx (nr_atendimento_retorno_p bigint, nr_atend_original_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
nr_seq_queixa_retorno_w		atendimento_paciente.nr_seq_queixa%type;
ie_tipo_atend_retorno_w		atendimento_paciente.ie_tipo_atendimento%type;
ie_tipo_guia_retorno_w		atend_categoria_convenio.ie_tipo_guia%type;
nr_seq_classif_retorno_w	atendimento_paciente.nr_seq_classificacao%type;
nr_seq_atecaco_w		atend_categoria_convenio.nr_seq_interno%type;
BEGIN
 
nr_seq_queixa_retorno_w := Obter_Param_Usuario(916, 293, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, nr_seq_queixa_retorno_w);
ie_tipo_atend_retorno_w := Obter_Param_Usuario(916, 782, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_tipo_atend_retorno_w);
ie_tipo_guia_retorno_w := Obter_Param_Usuario(916, 806, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_tipo_guia_retorno_w);
nr_seq_classif_retorno_w := Obter_Param_Usuario(916, 1031, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, nr_seq_classif_retorno_w);
 
update	atendimento_paciente 
set	nr_atend_original = nr_atend_original_p, 
	dt_atend_original = clock_timestamp(), 
	nm_usuario = nm_usuario_p, 
	dt_atualizacao = clock_timestamp(), 
	ie_tipo_atendimento = CASE WHEN ie_tipo_atend_retorno_w = NULL THEN  ie_tipo_atendimento WHEN ie_tipo_atend_retorno_w=0 THEN  ie_tipo_atendimento  ELSE ie_tipo_atend_retorno_w END , 
	nr_seq_classificacao = CASE WHEN nr_seq_classif_retorno_w = NULL THEN  nr_seq_classificacao WHEN nr_seq_classif_retorno_w=0 THEN  nr_seq_classificacao  ELSE nr_seq_classif_retorno_w END , 
	nr_seq_queixa = coalesce(nr_seq_queixa, nr_seq_queixa_retorno_w) 
where	nr_atendimento = nr_atendimento_retorno_p;
 
select	coalesce(max(nr_seq_interno),0) 
into STRICT	nr_seq_atecaco_w 
from 	atend_categoria_convenio a 
where	a.nr_atendimento = nr_atendimento_retorno_p 
and 	dt_inicio_vigencia = (	SELECT	max(dt_inicio_vigencia) 
				from	atend_categoria_convenio b 
				where	nr_atendimento = nr_atendimento_retorno_p);
 
update	atend_categoria_convenio a 
set	ie_tipo_guia = CASE WHEN ie_tipo_guia_retorno_w = NULL THEN  ie_tipo_guia  ELSE ie_tipo_guia_retorno_w END  
where	nr_atendimento = nr_atendimento_retorno_p 
and	nr_seq_interno = nr_seq_atecaco_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
