CREATE OR REPLACE PROCEDURE atualizar_atendimento ( nr_atendimento_p bigint, nr_prescricao_p bigint, cd_setor_p bigint ) AS $body$
DECLARE

qt_existe_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_existe_w
from 	prescr_medica
where 	nr_prescricao = nr_prescricao_p;

if (qt_existe_w > 0) then

	if (cd_setor_p IS NOT NULL AND cd_setor_p::text <> '') then
		update 	prescr_medica
		set 	nr_atendimento 		= nr_atendimento_p,
			cd_setor_atendimento 	= coalesce(cd_setor_atendimento, cd_setor_p)
		where 	nr_prescricao 		= nr_prescricao_p;
	else
		update 	prescr_medica
		set 	nr_atendimento 		= nr_atendimento_p
		where 	nr_prescricao 		= nr_prescricao_p;
	end if;
end if;

commit;

end;
