nr_seq_nova_agenda_w	agenda_integrada_item.nr_seq_agenda_cons%type;
				

BEGIN

Select 	max(nr_sequencia)
into STRICT	nr_seq_nova_agenda_w
from	agenda_consulta
where	nr_Atendimento = nr_atendimento_p;

