CREATE OR REPLACE PROCEDURE atualizar_atend_cartao_cheque ( nr_seq_caixa_rec_p bigint) AS $body$
DECLARE


nr_atendimento_w	adiantamento.nr_atendimento%type;


BEGIN

select 	coalesce(max(nr_atendimento),0)
into STRICT 	nr_atendimento_w
from	adiantamento
where 	nr_seq_caixa_rec = nr_seq_caixa_rec_p;

if (nr_atendimento_w > 0) then

	update 	cheque_cr
	set		nr_seq_atend_adiant = nr_atendimento_w
	where 	nr_seq_caixa_rec = nr_seq_caixa_rec_p;

	update 	movto_cartao_cr
	set		nr_seq_atend_adiant = nr_atendimento_w
	where 	nr_seq_caixa_rec = nr_seq_caixa_rec_p;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
