CREATE OR REPLACE PROCEDURE atualizar_atend_evolucao ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_vincular_w		varchar(10);
qt_validade_w		double precision;
cd_pessoa_fisica_w	varchar(10);
cd_evolucao_w		bigint;			
 
C01 CURSOR FOR 
	SELECT	cd_evolucao 
	from	evolucao_paciente 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w 
	and	coalesce(nr_atendimento::text, '') = '' 
	and	coalesce(dt_inativacao::text, '') = '' 
	and	((dt_evolucao > clock_timestamp() - qt_validade_w) or (qt_validade_w = 0));
					

BEGIN 
begin 
ie_vincular_w	:= Obter_Valor_Param_Usuario(281,489, Obter_Perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento);
 
if (ie_vincular_w	= 'S') then 
 
	qt_validade_w	:= dividir(Obter_Valor_Param_Usuario(281,490, Obter_Perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento),24);
	 
	select	cd_pessoa_fisica 
	into STRICT	cd_pessoa_fisica_w 
	from	atendimento_paciente 
	where	nr_atendimento	= nr_atendimento_p;
	 
	open C01;
	loop 
	fetch C01 into	 
		cd_evolucao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		update	evolucao_paciente 
		set	nr_atendimento	= nr_atendimento_p 
		where	cd_evolucao	= cd_evolucao_w;
		end;
	end loop;
	close C01;
	 
end if;
exception 
	when others then 
	null;
	end;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
