CREATE OR REPLACE PROCEDURE atualizar_atend_glicemia (nr_seq_glicemia_p bigint, ie_acao_p text, dt_acao_p timestamp, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_glicemia_p IS NOT NULL AND nr_seq_glicemia_p::text <> '') and (ie_acao_p IS NOT NULL AND ie_acao_p::text <> '') and (dt_acao_p IS NOT NULL AND dt_acao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	if (ie_acao_p = 'I') then -- iniciar
		update	atend_glicemia
		set	ie_status_glic	= 'N',
			cd_pf_inic_glic	= obter_dados_usuario_opcao(nm_usuario_p, 'C'),
			dt_inicio_glic	= dt_acao_p
		where	nr_sequencia		= nr_seq_glicemia_p;
	end if;
end if;

commit;

end;
$body$
