SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_atend_lib_financeira ( nr_atendimento_p bigint, ie_tipo_p text, nm_usuario_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE

 
ds_mensagem_w			varchar(500);
ds_pessoa_atend_w		varchar(255);
dt_alta_medico_w		timestamp;
ie_permite_sem_alta_medica_w	varchar(1);
qt_rn_sem_alta_w		integer;

BEGIN
 
ie_permite_sem_alta_medica_w := Obter_param_Usuario(44, 297, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_permite_sem_alta_medica_w);
 
if (coalesce(ie_tipo_p,'X') = 'L') and (ie_permite_sem_alta_medica_w <> 'S') then 
 
	select	max(dt_alta_medico) 
	into STRICT	dt_alta_medico_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
	if (coalesce(dt_alta_medico_w::text, '') = '') and (ie_permite_sem_alta_medica_w = 'N') then 
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(274943);
		 
	elsif (ie_permite_sem_alta_medica_w = 'R') then	 
		select 	count(1) 
		into STRICT	qt_rn_sem_alta_w 
		from	atendimento_paciente 
		where	nr_atendimento_mae = nr_atendimento_p 
		and	coalesce(dt_alta_medico::text, '') = '';
		 
		if (qt_rn_sem_alta_w > 0) and (coalesce(dt_alta_medico_w::text, '') = '') then 
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(349868);
		elsif (qt_rn_sem_alta_w > 0) then 
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(349867);
		end if;
	end if;
end if;
 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	update	atendimento_paciente 
	set	dt_liberacao_financeiro = CASE WHEN ie_tipo_p='L' THEN clock_timestamp() WHEN ie_tipo_p='D' THEN Null END  
	where	nr_atendimento 		= nr_atendimento_p;
 
	if (coalesce(ie_tipo_p,'X') = 'L') then 
		 
		select 	substr(obter_pessoa_atendimento(nr_atendimento_p,'N'),1,255) 
		into STRICT	ds_pessoa_atend_w 
		;
		 
		ds_mensagem_w	:= substr(obter_texto_dic_objeto(219349,philips_param_pck.get_nr_seq_idioma,'DS_PESSOA_ATEND_W='||ds_pessoa_atend_w||';'),1,500);
		 
	elsif (coalesce(ie_tipo_p,'X') = 'D') then 
		 
		select 	substr(obter_pessoa_atendimento(nr_atendimento_p,'N'),1,255) 
		into STRICT	ds_pessoa_atend_w 
		;	
		 
		ds_mensagem_w	:= substr(obter_texto_dic_objeto(219350,philips_param_pck.get_nr_seq_idioma,'DS_PESSOA_ATEND_W='||ds_pessoa_atend_w||';'),1,500);
	end if;
 
	if (ds_mensagem_w IS NOT NULL AND ds_mensagem_w::text <> '') then 
		ds_mensagem_p := ds_mensagem_w;
	end if;	
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
