CREATE OR REPLACE PROCEDURE atualizar_atend_lista_esp ( nr_atendimento_p bigint, nr_sequencia_p bigint, ie_excluir_paciente_p text) AS $body$
DECLARE

 
ie_alterar_w	varchar(1);


BEGIN 
 
select 	coalesce(max('S'),'N') 
into STRICT	ie_alterar_w 
from 	atendimento_paciente 
where 	nr_atendimento = nr_atendimento_p;
 
 
if (ie_alterar_w = 'S') then 
 
	update 	paciente_espera 
	set 	nr_atendimento = nr_atendimento_p 
	where 	nr_sequencia = nr_sequencia_p 
	and 	coalesce(nr_atendimento::text, '') = '';
	commit;
end if;
 
if (ie_excluir_paciente_p = 'S') then 
	delete 	FROM paciente_espera 
	where 	nr_sequencia = nr_sequencia_p;
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
