CREATE OR REPLACE PROCEDURE atualizar_atend_paciente ( nr_atendimento_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

/* 
ie_opcao: 
 
VPA - DT_VER_PREV_ALTA 
*/
 

BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	if (ie_opcao_p = 'VPA') then 
		begin 
		update	atendimento_paciente 
		set	dt_ver_prev_alta	= clock_timestamp(), 
			dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p 
		where	nr_atendimento		= nr_atendimento_p;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
