SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_ativ_usuario (nr_sequencia_p bigint) AS $body$
DECLARE


qt_min_ativ_w		bigint;
qt_min_os_w		bigint;
dt_entrada_w		timestamp;
dt_saida_w		timestamp;
nm_usuario_w		varchar(15);
qt_min_trab_w		integer;
ie_considera_dt_fim_w	varchar(01);
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

begin
select	max(PKG_DATE_UTILS.start_of(dt_entrada, 'dd', 0)),
	max(nm_usuario),
	max(round(((coalesce(dt_saida, clock_timestamp()) - dt_entrada) * 1440),0)),
	max(dt_saida)
into STRICT	dt_entrada_w,
	nm_usuario_w,
	qt_min_trab_w,
	dt_saida_w
from	usuario_controle
where	nr_sequencia		= nr_sequencia_p;

ie_considera_dt_fim_w := obter_param_usuario(299, 206, obter_perfil_Ativo, nm_usuario_w, cd_estabelecimento_w, ie_considera_dt_fim_w);

select	coalesce(sum(qt_minuto),0)
into STRICT	qt_min_ativ_w
from	usuario_atividade
where	nr_seq_controle		= nr_sequencia_p;

select	coalesce(sum(coalesce(qt_minuto,0)),0)
into STRICT	qt_min_os_w
from	man_ordem_serv_ativ
where	dt_atividade between dt_entrada_w and dt_entrada_w + 86399/86400
and	nm_usuario_exec	= nm_usuario_w;

if (coalesce(ie_considera_dt_fim_w,'N') = 'S') then
	select	coalesce(sum(coalesce(qt_minuto,0)),0)
	into STRICT	qt_min_os_w
	from	man_ordem_serv_ativ
	where	dt_atividade between dt_entrada_w and dt_saida_w
	and	nm_usuario_exec	= nm_usuario_w;
end if;

update	usuario_controle
set	qt_min_atividade		= qt_min_ativ_w,
	qt_min_os		= qt_min_os_w,
	qt_min_total		= qt_min_trab_w,
	qt_min_nreg		= qt_min_trab_w - qt_min_os_w - qt_min_ativ_w - coalesce(qt_min_intervalo,0) - coalesce(QT_MIN_LANCHE,0)
where	nr_sequencia		= nr_sequencia_p;

commit;

exception when others then
		/*(-20011,	'Não é possível atualizar os minutos de atividade do usuário.' || chr(10) ||
						'Verifique a data de entrada/saída (Usuário controle).');*/
		CALL wheb_mensagem_pck.exibir_mensagem_abort(263066);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_ativ_usuario (nr_sequencia_p bigint) FROM PUBLIC;

