CREATE OR REPLACE PROCEDURE atualizar_atraso_cirurgia ( nr_cirurgia_p bigint, nr_seq_motivo_p bigint, qt_tempo_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE

nr_sequencia_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	cir_paciente_atraso a
where	nr_cirurgia = nr_cirurgia_p
and	nr_seq_motivo = (SELECT	max(nr_seq_motivo)
			from	cir_paciente_atraso
			where	nr_cirurgia = nr_cirurgia_p);

update	cir_paciente_atraso
set	nr_seq_motivo  		= nr_seq_motivo_p,
	qt_tempo 		= qt_tempo_p,
	dt_atualizacao 		= clock_timestamp(),
	nm_usuario 		= nm_usuario_p,
	dt_atualizacao_nrec 	= clock_timestamp(),
	nm_usuario_nrec 	= nm_usuario_p,
	ds_observacao 		= ds_observacao_p
where	nr_sequencia 		= nr_sequencia_w
and	nr_cirurgia 		= nr_cirurgia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
