CREATE OR REPLACE PROCEDURE atualizar_auditoria_corrente ( nr_seq_grupo_atual_p bigint, nr_seq_analise_p bigint, nm_usuario_p text, ds_auditoria_cor_p INOUT text) AS $body$
DECLARE

 
nm_auditor_atual_w	varchar(255);


BEGIN 
 
ds_auditoria_cor_p	:= '';
 
if (nr_seq_grupo_atual_p > 0) then 
	nm_auditor_atual_w := pls_obter_se_auditor_dif(nr_seq_grupo_atual_p, nr_seq_analise_p, nm_usuario_p, nm_auditor_atual_w);
	if (coalesce(nm_auditor_atual_w,'') = '') then 
		CALL pls_atualizar_auditor_grupo(nr_seq_grupo_atual_p, nr_seq_analise_p, nm_usuario_p);
	elsif (nm_usuario_p <> nm_auditor_atual_w) then 
		ds_auditoria_cor_p := obter_texto_dic_objeto(92699, wheb_usuario_pck.get_nr_seq_idioma, 'NM_AUDITOR=' || substr(obter_nome_usuario(nm_auditor_atual_w),1,100) || ';');
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
