SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_barras_material_lote ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
cd_material_w		material.cd_material%type;
cd_material_barras_w	material.cd_material%type;
cd_barra_material_w	material_lote_fornec.cd_barra_material%type;
ie_barras_lote_fornec_w	parametro_estoque.ie_barras_lote_fornec%type;
qt_existe_w		bigint;
cd_cgc_w		material_lote_fornec.cd_cgc_fornec%type;


BEGIN
select	cd_estabelecimento,
	cd_cgc_fornec,
	cd_material,
	cd_barra_material
into STRICT	cd_estabelecimento_w,
	cd_cgc_w,
	cd_material_w,
	cd_barra_material_w
from	material_lote_fornec
where	nr_sequencia = nr_seq_lote_p;

select	coalesce(max(ie_barras_lote_fornec), 'N')
into STRICT	ie_barras_lote_fornec_w
from	parametro_estoque
where	cd_estabelecimento	= cd_estabelecimento_w;

if (ie_barras_lote_fornec_w = 'S') and (coalesce(cd_barra_material_w, '0') <> '0') then
	begin
	select	coalesce(max(cd_material),0)
	into STRICT	cd_material_barras_w
	from	material_cod_barra
	where	cd_barra_material = cd_barra_material_w
	and	cd_material <> cd_material_w;

	if (cd_material_barras_w > 0) then		
		CALL wheb_mensagem_pck.exibir_mensagem_abort(265565,'CD_MATERIAL=' || cd_material_w || ';CD_MATERIAL_LOTE=' || cd_material_barras_w || ';CD_BARRA_MATERIAL=' || cd_barra_material_w);		
	end if;
	
	select	count(*)
	into STRICT	qt_existe_w
	from	material_cod_barra
	where	cd_barra_material	= cd_barra_material_w;
	
	if (qt_existe_w = 0) then
		insert into material_cod_barra(
			cd_material,
			cd_barra_material,
			dt_atualizacao,
			nm_usuario,
			cd_cgc_fabricante,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_lote_fornec)
		values (	cd_material_w,
			cd_barra_material_w,
			clock_timestamp(),
			nm_usuario_p,
			cd_cgc_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_lote_p);
	end if;
	end;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
