SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_boletim_informativo ( nr_seq_tipo_p bigint) AS $body$
DECLARE



dt_evolucao_w		timestamp;
nr_atendimento_w	bigint;
ds_evolucao_w		varchar(30000);
nm_usuario_w		varchar(15);
dt_atualizacao_w	timestamp;
nr_sequencia_w		bigint;
nr_cont_w		bigint := 0;

C01 CURSOR FOR
SELECT	dt_evolucao,
	nr_atendimento,
	ds_evolucao,
	nm_usuario,
	dt_atualizacao
from	evolucao_paciente
where	ie_tipo_evolucao = nr_seq_tipo_p
and	(nr_atendimento IS NOT NULL AND nr_atendimento::text <> '');


BEGIN

open C01;
loop
	fetch C01 into 	dt_evolucao_w,
			nr_atendimento_w,
			ds_evolucao_w,
			nm_usuario_w,
			dt_atualizacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	select	nextval('atendimento_boletim_seq')
	into STRICT	nr_sequencia_w
	;

	insert into atendimento_boletim(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_atendimento,
		dt_boletim,
		ds_boletim)
	values (	nr_sequencia_w,
		dt_atualizacao_w,
		nm_usuario_w,
		nr_atendimento_w,
		dt_evolucao_w,
		substr(ds_evolucao_w,1,4000));

	nr_cont_w	:= nr_cont_w + 1;
	if (nr_cont_w = 1000) then
		commit;
		nr_cont_w := 0;
	end if;

	end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
