CREATE OR REPLACE PROCEDURE atualizar_cheque_baixa_ident ( nr_seq_cheque_p bigint, nr_seq_movto_pend_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_observacao_w			varchar(4000);
ds_observacao_update_w		varchar(4000);


BEGIN
if (nr_seq_cheque_p IS NOT NULL AND nr_seq_cheque_p::text <> '') then
	select	ds_observacao
	into STRICT	ds_observacao_w
	from	cheque_cr
	where	nr_seq_cheque = nr_seq_cheque_p;

	if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
		ds_observacao_update_w := substr(ds_observacao_w || chr(13) || Wheb_mensagem_pck.get_texto(303906) || ' ' || nr_seq_movto_pend_p||'.',1,4000);
	else
		ds_observacao_update_w := substr(Wheb_mensagem_pck.get_texto(303906) || ' ' || nr_seq_movto_pend_p ||'.',1,4000);
	end if;

	update	cheque_cr
	set	ds_observacao 	= ds_observacao_update_w,
		dt_devolucao	= clock_timestamp(),
		dt_atualizacao	= clock_timestamp(),
		nm_usuario     	= nm_usuario_p
	where	nr_seq_cheque 	= nr_seq_cheque_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
