CREATE OR REPLACE PROCEDURE atualizar_cid_cirurgia ( nr_seq_descricao_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_doenca_cid_w		varchar(10);	
cd_medico_w		varchar(10);
nr_atendimento_w	bigint;
qt_reg_w		bigint;		
			 

BEGIN 
 
select 	max(b.cd_doenca_cid), 
	max(a.nr_atendimento), 
	max(coalesce(b.cd_responsavel,a.cd_medico_cirurgiao)) 
into STRICT  	cd_doenca_cid_w, 
	nr_atendimento_w, 
	cd_medico_w 
from  	cirurgia a, 
	cirurgia_descricao b 
where 	a.nr_cirurgia = b.nr_cirurgia 
and  	b.nr_sequencia = nr_seq_descricao_p;
 
if (cd_doenca_cid_w IS NOT NULL AND cd_doenca_cid_w::text <> '') and (nr_atendimento_w > 0) then 
	 
	CALL Gerar_diagnostico_Atend(nr_atendimento_w,cd_doenca_cid_w,cd_medico_w,nm_usuario_p,2,'P',clock_timestamp());
	 
end if;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
