CREATE OR REPLACE PROCEDURE atualizar_cid_rotina ( cd_medico_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, vl_parametro_p INOUT text, cd_esp_medico_p INOUT text, ds_esp_medico_p INOUT text) AS $body$
DECLARE

 
