BEGIN 
 
if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then 
