CREATE OR REPLACE PROCEDURE atualizar_cirurgia_mat_log ( nr_prescricao_p bigint, nr_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE



ie_log_incl_excl_w	varchar(1);


BEGIN

ie_log_incl_excl_w	:= coalesce(obter_valor_param_usuario(900, 205, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento),'N');

if (ie_log_incl_excl_w = 'S') and (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_cirurgia_p,0) > 0) then

    update mat_atend_pac_log
    set    nr_cirurgia = nr_cirurgia_p
    where  nr_prescricao = nr_prescricao_p
    and    coalesce(nr_cirurgia::text, '') = '';

commit;

end if;


end;
$body$
