SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_classif_baixa_mens ( nr_seq_mensalidade_p bigint, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w			bigint;
nr_seq_baixa_w			bigint;
cd_estabelecimento_w		smallint;
ds_titulos_contab_w		varchar(4000);
nr_titulo_contab_w		bigint;

c01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		b.cd_estabelecimento 
	from	titulo_receber b, 
		titulo_receber_liq a 
	where	a.nr_titulo	= b.nr_titulo 
	and	b.nr_titulo	= nr_titulo_w;


BEGIN 
 
if (nr_seq_mensalidade_p IS NOT NULL AND nr_seq_mensalidade_p::text <> '') then 
	select	max(nr_titulo) 
	into STRICT	nr_titulo_w 
	from	titulo_receber 
	where	nr_seq_mensalidade	= nr_seq_mensalidade_p;
	 
	if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then 
		ds_titulos_contab_w	:= null;
		 
		open c01;
		loop 
		fetch c01 into 
			nr_seq_baixa_w, 
			cd_estabelecimento_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			 
			delete	from titulo_rec_liq_cc 
			where	nr_titulo	= nr_titulo_w 
			and	nr_seq_baixa	= nr_seq_baixa_w;
			 
			CALL gerar_titulo_rec_liq_cc(cd_estabelecimento_w, 
						null, 
						nm_usuario_p, 
						nr_titulo_w, 
						nr_seq_baixa_w);
						 
			nr_titulo_contab_w	:= null;
			 
			nr_titulo_contab_w := pls_gerar_tit_rec_liq_mens(	nr_titulo_w, nr_seq_baixa_w, nm_usuario_p, nr_titulo_contab_w);
		 
			if (nr_titulo_contab_w IS NOT NULL AND nr_titulo_contab_w::text <> '') then 
				ds_titulos_contab_w	:= substr(ds_titulos_contab_w || nr_titulo_contab_w || ', ',1,4000);
			end if;	
			 
		end loop;
		close c01;
	end if;
	 
	if (ds_titulos_contab_w IS NOT NULL AND ds_titulos_contab_w::text <> '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(236517, 'NR_TITULO=' || substr(ds_titulos_contab_w,1,length(ds_titulos_contab_w) -2) );
	end if;
end if;
 
if (coalesce(ie_commit_p,'S') = 'S') then 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
