CREATE OR REPLACE PROCEDURE atualizar_classif_relat_wheb ( cd_relatorio_wheb_p bigint, cd_funcao_p bigint) AS $body$
DECLARE

 
cd_classif_relat_wheb_w		varchar(4);
nr_seq_relatorio_w		bigint;

BEGIN
 
begin 
Select	r.nr_sequencia, 
	r.cd_classif_relat 
into STRICT	nr_seq_relatorio_w, 
	cd_classif_relat_wheb_w 
from	relatorio r, 
	relatorio_funcao rf 
where	r.nr_sequencia = rf.nr_seq_relatorio 
and	rf.cd_funcao = cd_funcao_p 
and	r.cd_relatorio = cd_relatorio_wheb_p;
 
update	relatorio 
set	cd_classif_relat_wheb = cd_classif_relat_wheb_w 
where	nr_sequencia = nr_seq_relatorio_w;
 
commit;
 
exception 
when others then 
	nr_seq_relatorio_w	:= 0;
end;
END;
$body$
