SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_cni_baixa_cartao (cd_estabelecimento_p bigint, nr_seq_movto_cartao_p bigint, nr_seq_lote_cartao_p bigint, ie_acao_p text) AS $body$
DECLARE


nr_seq_movto_trans_fin_w	bigint;
nr_seq_movto_pend_baixa_w	bigint;
ie_forma_lanc_lote_cartao_w	varchar(1);
nr_seq_parcela_w		bigint;
v_baixa_parcela_w		double precision;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	movto_cartao_cr_parcela a
where	a.nr_seq_lote	= nr_seq_lote_cartao_p;


BEGIN

select	max(ie_forma_lanc_lote_cartao)
into STRICT	ie_forma_lanc_lote_cartao_w
from	parametro_contas_receber
where	cd_estabelecimento	= cd_estabelecimento_p;

if (nr_seq_movto_cartao_p IS NOT NULL AND nr_seq_movto_cartao_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_movto_pend_baixa_w
	from	movto_banco_pend_baixa
	where	nr_seq_cartao_baixa	= nr_seq_movto_cartao_p;

	select	max(d.nr_sequencia)
	into STRICT	nr_seq_movto_trans_fin_w
	from	transacao_financeira e,
		movto_trans_financ d,
		movto_cartao_cr c,
		movto_cartao_cr_parcela b,
		movto_cartao_cr_baixa a
	where	a.nr_seq_parcela	= b.nr_sequencia
	and	b.nr_seq_movto		= c.nr_sequencia
	and	d.nr_seq_movto_cartao	= c.nr_sequencia
	and	e.nr_sequencia		= d.nr_seq_trans_financ
	and	e.ie_acao		= 24
	and	trunc(d.dt_transacao)	= trunc(a.dt_baixa)
	and	a.nr_sequencia		= nr_seq_movto_cartao_p;

	if (nr_seq_movto_trans_fin_w IS NOT NULL AND nr_seq_movto_trans_fin_w::text <> '') then
		update	movto_banco_pend_baixa
		set	nr_seq_movto_trans_fin	= nr_seq_movto_trans_fin_w
		where	nr_sequencia		= nr_seq_movto_pend_baixa_w;
	end if;

elsif (nr_seq_lote_cartao_p IS NOT NULL AND nr_seq_lote_cartao_p::text <> '') then


	if (coalesce(ie_forma_lanc_lote_cartao_w,'P') = 'L') then /*Uma baixa para o lote*/
		select	max(nr_sequencia)
		into STRICT	nr_seq_movto_pend_baixa_w
		from	movto_banco_pend_baixa
		where	nr_seq_lote_cartao	= nr_seq_lote_cartao_p;

		if (ie_acao_p = 'I') then

			select	max(b.nr_sequencia)
			into STRICT	nr_seq_movto_trans_fin_w
			from	transacao_financeira c,
				movto_trans_financ b,
				lote_baixa_cartao_cr a
			where	b.nr_seq_lote_cartao	= a.nr_sequencia
			and	c.nr_sequencia		= b.nr_seq_trans_financ
			and	c.ie_acao		= 24
			and	trunc(b.dt_transacao)	= trunc(a.dt_baixa)
			and	a.nr_sequencia		= nr_seq_lote_cartao_p;

		end if;

		if (nr_seq_movto_trans_fin_w IS NOT NULL AND nr_seq_movto_trans_fin_w::text <> '') then
			update	movto_banco_pend_baixa
			set	nr_seq_movto_trans_fin	= nr_seq_movto_trans_fin_w
			where	nr_sequencia		= nr_seq_movto_pend_baixa_w;
		end if;
	else

		open C01;
		loop
		fetch C01 into
			nr_seq_parcela_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */

			select	max(nr_sequencia)
			into STRICT	nr_seq_movto_pend_baixa_w
			from	movto_banco_pend_baixa
			where	nr_seq_lote_cartao	= nr_seq_lote_cartao_p
			and	nr_seq_parc_cartao	= nr_seq_parcela_w;

			select	max(d.nr_sequencia)
			into STRICT	nr_seq_movto_trans_fin_w
			from	transacao_financeira e,
				movto_trans_financ d,
				movto_cartao_cr c,
				movto_cartao_cr_parcela b
			where	b.nr_seq_movto		= c.nr_sequencia
			and	d.nr_seq_movto_cartao	= c.nr_sequencia
			and	d.nr_seq_parc_cartao	= b.nr_sequencia
			and	e.nr_sequencia		= d.nr_seq_trans_financ
			and	e.ie_acao		= 24
			and	b.nr_sequencia		= nr_seq_parcela_w;

			if (nr_seq_movto_trans_fin_w IS NOT NULL AND nr_seq_movto_trans_fin_w::text <> '') then
				update	movto_banco_pend_baixa
				set	nr_seq_movto_trans_fin	= nr_seq_movto_trans_fin_w
				where	nr_sequencia		= nr_seq_movto_pend_baixa_w
				and	nr_seq_parc_cartao	= nr_seq_parcela_w;
			end if;

		end loop;
		close C01;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualizar_cni_baixa_cartao (cd_estabelecimento_p bigint, nr_seq_movto_cartao_p bigint, nr_seq_lote_cartao_p bigint, ie_acao_p text) FROM PUBLIC;

