CREATE OR REPLACE PROCEDURE atualizar_cobranca_etapa (nr_seq_cobranca_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_etapa_w	bigint;


BEGIN

begin
select	nr_seq_etapa
into STRICT	nr_seq_etapa_w
from (SELECT	a.nr_seq_etapa
	from	cobranca_etapa a
	where	a.nr_seq_cobranca	= nr_seq_cobranca_p
	order	by a.dt_etapa desc) alias0 LIMIT 1;
exception
when others then
	nr_seq_etapa_w	:= null;
end;

update	cobranca
set	nr_seq_etapa	= nr_seq_etapa_w,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_cobranca_p;

commit;

end;
