CREATE OR REPLACE PROCEDURE atualizar_cobranca_historico ( nr_seq_cobranca_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_portador_w		bigint;
cd_tipo_portador_w	integer;
cd_tipo_portador_ww	integer;


BEGIN

select	coalesce(max(b.cd_portador),0),
	coalesce(max(b.cd_tipo_portador),0)
into STRICT	cd_portador_w,
	cd_tipo_portador_w
from	cobranca_historico a,
	tipo_hist_cob b
where	a.nr_seq_historico = b.nr_sequencia
and	a.nr_sequencia = (	SELECT	max(nr_sequencia)
				from 	cobranca_historico
				where 	nr_seq_cobranca = nr_seq_cobranca_p);

if (cd_portador_w > 0) and (cd_tipo_portador_w > 0) then

	update	cobranca
	set	cd_portador 		= cd_portador_w,
		cd_tipo_portador	= cd_tipo_portador_w
	where	nr_sequencia		= nr_seq_cobranca_p;

elsif (cd_portador_w > 0) and (cd_tipo_portador_w = 0) then

	select	max(cd_tipo_portador)
	into STRICT	cd_tipo_portador_ww
	from	portador
	where	cd_portador = cd_portador_w;

	update	cobranca
	set	cd_portador 		= cd_portador_w,
		cd_tipo_portador	= cd_tipo_portador_ww
	where	nr_sequencia		= nr_seq_cobranca_p;

elsif (cd_portador_w = 0) and (cd_tipo_portador_w > 0) then

	update	cobranca
	set	cd_portador 		 = NULL,
		cd_tipo_portador	= cd_tipo_portador_w
	where	nr_sequencia		= nr_seq_cobranca_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
