SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_com_cliente_proposta ( nr_sequencia_p bigint, ie_opcao_p text, ie_prev_implant_p text, nm_usuario_p text) AS $body$
DECLARE

/*
Opcoes:
- C 	= Cancelamento;
- EC	= Estornar cancelamento;
- A	= Aprovada;
- EA	= Estornar aprovacao;
*/
BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	if (ie_opcao_p = 'C') then
		begin

		update	com_cliente_proposta
		set	dt_cancelamento		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			nm_usuario_cancelamento = nm_usuario_p
		where	nr_sequencia		= nr_sequencia_p;

		if (ie_prev_implant_p = 'S') then
			begin
			update	com_prev_implant
			set	dt_cancelamento		= clock_timestamp(),
				nm_usuario		= nm_usuario_p,
				nm_usuario_cancelamento	= nm_usuario_p
			where	nr_seq_proposta		= nr_sequencia_p;
			end;
		end if;
		end;
	elsif (ie_opcao_p = 'EC') then
		begin

		update	com_cliente_proposta
		set	dt_cancelamento		 = NULL,
			nm_usuario		= nm_usuario_p,
			nm_usuario_cancelamento  = NULL
		where	nr_sequencia		= nr_sequencia_p;
		end;
	elsif (ie_opcao_p = 'A') then
		begin

		update	com_cliente_proposta
		set	dt_aprovacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			nm_usuario_aprov	= nm_usuario_p
		where	nr_sequencia		= nr_sequencia_p;
		end;
	elsif (ie_opcao_p = 'EA') then
		begin

		update	com_cliente_proposta
		set	dt_aprovacao		 = NULL,
			nm_usuario		= nm_usuario_p,
			nm_usuario_aprov 	 = NULL
		where	nr_sequencia		= nr_sequencia_p;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
