SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_conpaci_desc_estrut (nr_seq_desconto_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_tipo_desconto_w	smallint;
vl_conta_w		double precision;
vl_desconto_w		double precision;


BEGIN

select	ie_tipo_desconto,
	vl_conta
into STRICT	ie_tipo_desconto_w,
	vl_conta_w
from	conta_paciente_desconto
where	nr_sequencia	= nr_seq_desconto_p;

select	coalesce(sum(vl_desconto),0)
into STRICT	vl_desconto_w
from	conta_paciente_desc_item
where	nr_seq_desconto		= nr_seq_desconto_p;


update	conta_paciente_desconto
set	vl_desconto	= vl_desconto_w,
	PR_DESCONTO	= dividir((vl_desconto_w * 100),vl_conta_w),
	VL_LIQUIDO	= vl_conta - vl_desconto_w,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_desconto_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
