SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_contas_contabeis ( nr_seq_lote_p bigint, ie_conta_med_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_mvto_contabil_w	bigint;
ds_retorno_w		varchar(255);


BEGIN

if (coalesce(nr_seq_lote_p,0) > 0) then

	select	count(*)
	into STRICT	qt_mvto_contabil_w
	from	lote_contabil a,
		movimento_contabil b
	where	b.nr_lote_contabil = a.nr_lote_contabil
	and	a.cd_tipo_lote_contabil = 21
	and	coalesce(a.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p
	and	trunc(a.dt_referencia,'month') = (
		SELECT trunc(max(y.dt_mesano_referencia),'month')
		from	pls_mensalidade x,
			pls_lote_mensalidade y
		where	x.nr_seq_lote = y.nr_sequencia
		and      y.nr_sequencia = nr_seq_lote_p);

	if (qt_mvto_contabil_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 195350, 'DS_ERRO='||obter_texto_dic_objeto(149521, wheb_usuario_pck.get_nr_seq_idioma, null));
	end if;

	ds_retorno_w := pls_atualizar_contas_receita(nr_seq_lote_p, null, nm_usuario_p, cd_estabelecimento_p, 'N', ds_retorno_w);

else

	select	count(*)
	into STRICT	qt_mvto_contabil_w
	from	lote_contabil a,
		movimento_contabil b
	where	b.nr_lote_contabil = a.nr_lote_contabil
	and	a.cd_tipo_lote_contabil = 21
	and	coalesce(a.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p
	and	trunc(a.dt_referencia,'month') = trunc(to_date('01/'||ie_conta_med_p),'month');
	
	if (qt_mvto_contabil_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort( 195350, 'DS_ERRO='||obter_texto_dic_objeto(149526, wheb_usuario_pck.get_nr_seq_idioma, 'ATUALIZAR_CONTA=' || ie_conta_med_p));
	end if;
	
	ds_retorno_w := pls_atualizar_conta_contabil('01/'||ie_conta_med_p, nm_usuario_p, cd_estabelecimento_p, 'M', ds_retorno_w);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
