CREATE OR REPLACE PROCEDURE atualizar_conta_bco_pag_escrit ( cd_pessoa_fisica_p text, cd_cgc_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_atualiza_w		varchar(1);
nr_titulo_w		bigint;
nr_seq_escrit_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_titulo,
		a.nr_seq_escrit
	from	titulo_pagar		b,
		titulo_pagar_escrit	a
	where	a.nr_titulo		= b.nr_titulo
	and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	exists (SELECT	1
			from	banco_escritural	x
			where	x.nr_sequencia	= a.nr_seq_escrit
			and	coalesce(x.ds_arquivo::text, '') = '')
	
union all

	select	a.nr_titulo,
		a.nr_seq_escrit
	from	titulo_pagar		b,
		titulo_pagar_escrit	a
	where	a.nr_titulo	= b.nr_titulo
	and	b.cd_cgc	= cd_cgc_p
	and	exists (select	1
			from	banco_escritural	x
			where	x.nr_sequencia	= a.nr_seq_escrit
			and	coalesce(x.ds_arquivo::text, '') = '');


BEGIN
ie_atualiza_w := obter_param_usuario(857, 39, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_w);

if (ie_atualiza_w = 'S') then
	open C01;
	loop
	fetch C01 into
		nr_titulo_w,
		nr_seq_escrit_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		CALL definir_banco_tit_escritural('CP',nr_titulo_w,nr_seq_escrit_w,nm_usuario_p,'N');
		end;
	end loop;
	close C01;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
