CREATE OR REPLACE PROCEDURE atualizar_controle_dig_laudo ( nr_seq_laudo_p bigint, nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_opcao_p text, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_prescr_w		integer;
ds_arquivo_w		varchar(80);


C01 CURSOR FOR
SELECT	a.nr_sequencia
from	prescr_proc_ditado b,
	prescr_procedimento a
where	a.nr_seq_interno	= b.nr_seq_prescr_proc
and	a.nr_prescricao		= nr_prescricao_p
and	b.ds_arquivo		= ds_arquivo_w;


BEGIN

begin
select	b.nr_sequencia,
	b.ds_arquivo
into STRICT	nr_sequencia_w,
	ds_arquivo_w
from	prescr_proc_ditado b,
	prescr_procedimento a
where	a.nr_seq_interno	= b.nr_seq_prescr_proc
and	a.nr_prescricao		= nr_prescricao_p
and	a.nr_sequencia		= nr_seq_prescricao_p;
exception
	when others then
		ds_arquivo_w	:= null;
end;

if (ie_opcao_p = 'I') then
	insert into controle_digitacao_laudo(
		nr_sequencia,
		nm_usuario,
		cd_pessoa_fisica,
		nr_prescricao,
		nr_seq_prescr)
	values (	nr_seq_laudo_p,
		nm_usuario_p,
		cd_pessoa_fisica_p,
		nr_prescricao_p,
		nr_seq_prescricao_p);

	insert into controle_digitacao_laudo(
		nr_sequencia,
		nm_usuario,
		cd_pessoa_fisica,
		nr_prescricao,
		nr_seq_prescr)
	SELECT	nr_seq_laudo_p,
		nm_usuario_p,
		cd_pessoa_fisica_p,
		nr_prescricao_p,
		a.nr_sequencia
	from	prescr_proc_ditado b,
		prescr_procedimento a
	where	a.nr_seq_interno	= b.nr_seq_prescr_proc
	and	a.nr_prescricao		= nr_prescricao_p
	and	a.nr_sequencia		<> nr_seq_prescricao_p
	and	b.nr_sequencia		<> nr_sequencia_w
	and	b.ds_arquivo		= ds_arquivo_w;

else
	delete	FROM controle_digitacao_laudo
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_prescr	= nr_seq_prescricao_p;

	open	c01;
	loop
	fetch	c01 into nr_seq_prescr_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		delete	FROM controle_digitacao_laudo
		where	nr_prescricao	= nr_prescricao_p
		and	nr_sequencia	= nr_seq_prescr_w;
		end;
	end 	loop;
	close	c01;
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
