CREATE OR REPLACE PROCEDURE atualizar_controle_prescr_eup ( nr_prescricao_p bigint, nr_seq_prescr_p bigint default 0, nr_controle_p bigint DEFAULT NULL, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE


ds_texto_w	varchar(255);


BEGIN

ds_texto_w	:= substr(obter_texto_dic_objeto(303795, wheb_usuario_pck.get_nr_seq_idioma, 'NR_PRESCRICAO_P='|| nr_prescricao_p || ';NR_SEQ_PRESCR_P=' || nr_seq_prescr_p || ';NR_CONTROLE_P=' || nr_controle_p || ';NM_USUARIO_P=' || nm_usuario_p),1,255);

CALL gravar_log_tasy(29, ds_texto_w, nm_usuario_p);

if (coalesce(nr_seq_prescr_p, 0) = 0) then
	update 	prescr_medica
	set 	nr_controle = nr_controle_p,
		nm_usuario = nm_usuario_p
	where 	nr_prescricao = nr_prescricao_p;
else
	UPDATE 	prescr_procedimento
	SET 	nr_controle_ext = nr_controle_p,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	WHERE 	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_seq_prescr_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
