SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_convites_agendas ( ds_lista_sequencias_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
ds_lista_w		varchar(1000);
ie_pos_virgula_w	smallint;
nr_seq_agenda_w		bigint;
dt_agenda_w		timestamp;
ie_confirmado_w		varchar(1);
ds_motivo_ausencia_w	varchar(255);


BEGIN

if (ds_lista_sequencias_p IS NOT NULL AND ds_lista_sequencias_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	ds_lista_w	:= ds_lista_sequencias_p;

	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
		begin

		ie_pos_virgula_w 	:= position(',' in ds_lista_w);

		if (ie_pos_virgula_w <> 0) then
			begin
			nr_sequencia_w		:= (substr(ds_lista_w,1,(ie_pos_virgula_w - 1)))::numeric;
			ds_lista_w		:= substr(ds_lista_w,(ie_pos_virgula_w + 1),length(ds_lista_w));
			end;
		else
			begin
			nr_sequencia_w		:= (ds_lista_w)::numeric;
			ds_lista_w		:= null;
			end;
		end if;

		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
			begin
			select	b.nr_seq_agenda,
				b.ie_confirmado,
				a.dt_agenda,
				b.ds_motivo_ausencia
			into STRICT	nr_seq_agenda_w,
				ie_confirmado_w,
				dt_agenda_w,
				ds_motivo_ausencia_w
			from	agenda_tasy_convite b,
				agenda_tasy a
			where	a.nr_sequencia = b.nr_seq_agenda
			and	b.nr_sequencia = nr_sequencia_w;

			if (nr_seq_agenda_w IS NOT NULL AND nr_seq_agenda_w::text <> '') and (ie_confirmado_w IS NOT NULL AND ie_confirmado_w::text <> '') and (dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then
				begin
				CALL atualizar_agenda_tasy_convite(
					nr_seq_agenda_w,
					dt_agenda_w,
					ie_confirmado_w,
					ds_motivo_ausencia_w,
					nm_usuario_p);
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
