SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_crm_atendimentos ( nr_atendimento_p text, nm_usuario_p text) AS $body$
DECLARE

 
vl_faturado_w			varchar(250);
ie_integracao_dynamics_w	varchar(1);
qt_crm_atendimentos_w		bigint;
					

BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
	select	coalesce(max(ie_integracao_dynamics),'N') 
	into STRICT	ie_integracao_dynamics_w 
	from	parametro_atendimento 
	where	cd_estabelecimento = obter_estab_atend(nr_atendimento_p);
 
	if (ie_integracao_dynamics_w = 'S') then 
		select	count(*) 
		into STRICT	qt_crm_atendimentos_w 
		from	crm_atendimentos 
		where	nr_atendimento = nr_atendimento_p;
	 
		if (qt_crm_atendimentos_w > 0) then 
			select	sum(obter_valor_conta(nr_interno_conta,0)) 
			into STRICT	vl_faturado_w 
			from	conta_paciente 
			where	nr_atendimento = nr_atendimento_p 
			and	((Obter_Tipo_Convenio(cd_convenio_parametro) <> 1 and coalesce(nr_protocolo,'0') <> '0') 
			or (Obter_Tipo_Convenio(cd_convenio_parametro) = 1 and (obter_nf_conta(nr_interno_conta,2) IS NOT NULL AND (obter_nf_conta(nr_interno_conta,2))::text <> '')));
 
			update	crm_atendimentos 
			set	vl_faturado 	= vl_faturado_w, 
				dt_atualizacao	= clock_timestamp(), 
				nm_usuario	= nm_usuario_p 
			where	nr_atendimento 	= nr_atendimento_p;
		end if;
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
