SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_crm_medico ( cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE

 
cd_cargo_w		bigint;
nr_conselho_w		varchar(20);


BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	 
	select	max(a.cd_cargo) 
	into STRICT	cd_cargo_w 
	from	pessoa_fisica a 
	where	a.cd_pessoa_fisica		= cd_pessoa_fisica_p;
	 
	if (cd_cargo_w IS NOT NULL AND cd_cargo_w::text <> '') then 
		 
		select	max(a.nr_conselho) 
		into STRICT	nr_conselho_w 
		from	cargo a 
		where	a.cd_cargo	= cd_cargo_w;
		 
		if (nr_conselho_w IS NOT NULL AND nr_conselho_w::text <> '') then 
			 
			update	medico 
			set	nr_crm			= nr_conselho_w, 
				nm_usuario		= nm_usuario_p 
			where	cd_pessoa_fisica		= cd_pessoa_fisica_p;
		end if;
		 
	end if;
end if;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
